mapper.sql:
select

from art_mail_date where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	md.id 
	, md.email 
	, md.send_time 
	, md.send_count 
	, md.trace_code_i 
	, md.trace_code_u 
	, md.host_name 
	, md.create_user_id 
	, md.create_by 
	, md.create_date 
	, md.update_user_id 
	, md.update_by 
	, md.update_date 
from art_mail_date md 
where 1 = 1

	and md.id = #{id}


	and md.email like concat('%', #{email}, '%')


	and date_format(md.send_time, '%Y-%m-%d') >= #{send_time_from}


	and date_format(md.send_time, '%Y-%m-%d') <= #{send_time_to}


	and date_format(md.send_time, '%Y-%m-%d') = #{send_time}


	and md.send_count = #{send_count}


	and md.trace_code_i like concat('%', #{trace_code_i}, '%')


	and md.trace_code_u like concat('%', #{trace_code_u}, '%')


	and md.host_name like concat('%', #{host_name}, '%')


	and md.create_user_id = #{create_user_id}


	and md.create_by like concat('%', #{create_by}, '%')


	and date_format(md.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(md.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(md.create_date, '%Y-%m-%d') = #{create_date}


	and md.update_user_id = #{update_user_id}


	and md.update_by like concat('%', #{update_by}, '%')


	and date_format(md.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(md.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(md.update_date, '%Y-%m-%d') = #{update_date}

order by md.id desc

mapper.insert:
insert into art_mail_date 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_mail_date where 1



	and ${key} is null


	and ${key} = #{value}
