mapper.sql:
select

from art_news where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select n.id , n.title , n.thumbnail , n.content , n.url , n.local_path , n.delete_status , n.trace_code_i , n.trace_code_u , n.host_name , n.create_user_id , n.create_by , n.create_date , n.update_user_id , n.update_by , n.update_date from art_news n where n.delete_status = 1
and n.id = #{id}
and n.title like concat('%', #{title}, '%')
and n.thumbnail like concat('%', #{thumbnail}, '%')
and n.content = #{content}
and n.url like concat('%', #{url}, '%')
and n.local_path like concat('%', #{local_path}, '%')
and n.trace_code_i like concat('%', #{trace_code_i}, '%')
and n.trace_code_u like concat('%', #{trace_code_u}, '%')
and n.host_name like concat('%', #{host_name}, '%')
and n.create_user_id = #{create_user_id}
and n.create_by like concat('%', #{create_by}, '%')
and date_format(n.create_date, '%Y-%m-%d') >= #{create_date_from}
and date_format(n.create_date, '%Y-%m-%d') <= #{create_date_to}
and date_format(n.create_date, '%Y-%m-%d') = #{create_date}
and n.update_user_id = #{update_user_id}
and n.update_by like concat('%', #{update_by}, '%')
and date_format(n.update_date, '%Y-%m-%d') >= #{update_date_from}
and date_format(n.update_date, '%Y-%m-%d') <= #{update_date_to}
and date_format(n.update_date, '%Y-%m-%d') = #{update_date}
order by n.id desc

mapper.insert:
insert into art_news 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_news where 1



	and ${key} is null


	and ${key} = #{value}
