mapper.sql:
select

from art_slider where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select s.id , s.url , s.block , s.link , s.description , s.delete_status , s.trace_code_i , s.trace_code_u , s.host_name , s.create_user_id , s.create_by , s.create_date , s.update_user_id , s.update_by , s.update_date from art_slider s where s.delete_status = 1
and s.id = #{id}
and s.url like concat('%', #{url}, '%')
and s.block = #{block}
and s.link like concat('%', #{link}, '%')
and s.description like concat('%', #{description}, '%')
and s.trace_code_i like concat('%', #{trace_code_i}, '%')
and s.trace_code_u like concat('%', #{trace_code_u}, '%')
and s.host_name like concat('%', #{host_name}, '%')
and s.create_user_id = #{create_user_id}
and s.create_by like concat('%', #{create_by}, '%')
and date_format(s.create_date, '%Y-%m-%d') >= #{create_date_from}
and date_format(s.create_date, '%Y-%m-%d') <= #{create_date_to}
and date_format(s.create_date, '%Y-%m-%d') = #{create_date}
and s.update_user_id = #{update_user_id}
and s.update_by like concat('%', #{update_by}, '%')
and date_format(s.update_date, '%Y-%m-%d') >= #{update_date_from}
and date_format(s.update_date, '%Y-%m-%d') <= #{update_date_to}
and date_format(s.update_date, '%Y-%m-%d') = #{update_date}
order by s.id desc

mapper.insert:
insert into art_slider 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_slider where 1



	and ${key} is null


	and ${key} = #{value}
