mapper.sql:
select

from art_thirdparty where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	t.id 
	, t.user_id 
	, t.third_name 
	, t.password 
	, t.type 
	, t.enable_status 
	, t.trace_code_i 
	, t.trace_code_u 
	, t.host_name 
	, t.create_user_id 
	, t.create_by 
	, t.create_date 
	, t.update_user_id 
	, t.update_by 
	, t.update_date 
from art_thirdparty t 
where 1 = 1

	and t.id = #{id}


	and t.user_id = #{user_id}


	and t.third_name like concat('%', #{third_name}, '%')


	and t.password like concat('%', #{password}, '%')


	and t.type = #{type}


	and t.enable_status = #{enable_status}


	and t.trace_code_i like concat('%', #{trace_code_i}, '%')


	and t.trace_code_u like concat('%', #{trace_code_u}, '%')


	and t.host_name like concat('%', #{host_name}, '%')


	and t.create_user_id = #{create_user_id}


	and t.create_by like concat('%', #{create_by}, '%')


	and date_format(t.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(t.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(t.create_date, '%Y-%m-%d') = #{create_date}


	and t.update_user_id = #{update_user_id}


	and t.update_by like concat('%', #{update_by}, '%')


	and date_format(t.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(t.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(t.update_date, '%Y-%m-%d') = #{update_date}

order by t.id desc

mapper.insert:
insert into art_thirdparty 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_thirdparty where 1



	and ${key} is null


	and ${key} = #{value}
