mapper.sql:
select

from art_user where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	u.id 
	, u.login_name 
	, u.password 
	, u.mobile 
	, u.email 
	, u.nick_name 
	, u.full_name 
	, u.gender 
	, u.age 
	, u.birthday 
	, u.avatar 
	, u.credit 
	, u.balance 
	, u.checkout_count 
	, u.last_checkout_time 
	, u.last_login_time 
	, u.login_count 
	, u.last_login_ip 
	, u.delete_status 
	, u.login_token 
	, u.login_token_refresh_time 
	, u.trace_code_i 
	, u.trace_code_u 
	, u.host_name 
	, u.create_user_id 
	, u.create_by 
	, u.create_date 
	, u.update_user_id 
	, u.update_by 
	, u.update_date 
from art_user u 
where u.delete_status = 1

	and u.id = #{id}


	and u.login_name like concat('%', #{login_name}, '%')


	and u.password like concat('%', #{password}, '%')


	and u.mobile like concat('%', #{mobile}, '%')


	and u.email like concat('%', #{email}, '%')


	and u.nick_name like concat('%', #{nick_name}, '%')


	and u.full_name like concat('%', #{full_name}, '%')


	and u.gender = #{gender}


	and u.age = #{age}


	and date_format(u.birthday, '%Y-%m-%d') >= #{birthday_from}


	and date_format(u.birthday, '%Y-%m-%d') <= #{birthday_to}


	and date_format(u.birthday, '%Y-%m-%d') = #{birthday}


	and u.avatar like concat('%', #{avatar}, '%')


	and u.credit = #{credit}


	and u.balance = #{balance}


	and u.checkout_count = #{checkout_count}


	and date_format(u.last_checkout_time, '%Y-%m-%d') >= #{last_checkout_time_from}


	and date_format(u.last_checkout_time, '%Y-%m-%d') <= #{last_checkout_time_to}


	and date_format(u.last_checkout_time, '%Y-%m-%d') = #{last_checkout_time}


	and date_format(u.last_login_time, '%Y-%m-%d') >= #{last_login_time_from}


	and date_format(u.last_login_time, '%Y-%m-%d') <= #{last_login_time_to}


	and date_format(u.last_login_time, '%Y-%m-%d') = #{last_login_time}


	and u.login_count = #{login_count}


	and u.last_login_ip like concat('%', #{last_login_ip}, '%')


	and u.login_token like concat('%', #{login_token}, '%')


	and date_format(u.login_token_refresh_time, '%Y-%m-%d') >= #{login_token_refresh_time_from}


	and date_format(u.login_token_refresh_time, '%Y-%m-%d') <= #{login_token_refresh_time_to}


	and date_format(u.login_token_refresh_time, '%Y-%m-%d') = #{login_token_refresh_time}


	and u.trace_code_i like concat('%', #{trace_code_i}, '%')


	and u.trace_code_u like concat('%', #{trace_code_u}, '%')


	and u.host_name like concat('%', #{host_name}, '%')


	and u.create_user_id = #{create_user_id}


	and u.create_by like concat('%', #{create_by}, '%')


	and date_format(u.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(u.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(u.create_date, '%Y-%m-%d') = #{create_date}


	and u.update_user_id = #{update_user_id}


	and u.update_by like concat('%', #{update_by}, '%')


	and date_format(u.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(u.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(u.update_date, '%Y-%m-%d') = #{update_date}

order by u.id desc

mapper.insert:
insert into art_user 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_user where 1



	and ${key} is null


	and ${key} = #{value}
