mapper.sql:
select

from art_admin_user where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	au.id 
	, au.login_name 
	, au.password 
	, au.role_id 
	, au.mobile 
	, au.email 
	, au.full_name 
	, au.gender 
	, au.birthday 
	, au.avatar 
	, au.last_login_time 
	, au.last_login_ip 
	, au.delete_status 
	, au.trace_code_i 
	, au.trace_code_u 
	, au.host_name 
	, au.create_user_id 
	, au.create_by 
	, au.create_date 
	, au.update_user_id 
	, au.update_by 
	, au.update_date 
from art_admin_user au 
where au.delete_status = 1

	and au.id = #{id}


	and au.login_name like concat('%', #{login_name}, '%')


	and au.password like concat('%', #{password}, '%')


	and au.role_id = #{role_id}


	and au.mobile like concat('%', #{mobile}, '%')


	and au.email like concat('%', #{email}, '%')


	and au.full_name like concat('%', #{full_name}, '%')


	and au.gender = #{gender}


	and date_format(au.birthday, '%Y-%m-%d') >= #{birthday_from}


	and date_format(au.birthday, '%Y-%m-%d') <= #{birthday_to}


	and date_format(au.birthday, '%Y-%m-%d') = #{birthday}


	and au.avatar like concat('%', #{avatar}, '%')


	and date_format(au.last_login_time, '%Y-%m-%d') >= #{last_login_time_from}


	and date_format(au.last_login_time, '%Y-%m-%d') <= #{last_login_time_to}


	and date_format(au.last_login_time, '%Y-%m-%d') = #{last_login_time}


	and au.last_login_ip like concat('%', #{last_login_ip}, '%')


	and au.trace_code_i like concat('%', #{trace_code_i}, '%')


	and au.trace_code_u like concat('%', #{trace_code_u}, '%')


	and au.host_name like concat('%', #{host_name}, '%')


	and au.create_user_id = #{create_user_id}


	and au.create_by like concat('%', #{create_by}, '%')


	and date_format(au.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(au.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(au.create_date, '%Y-%m-%d') = #{create_date}


	and au.update_user_id = #{update_user_id}


	and au.update_by like concat('%', #{update_by}, '%')


	and date_format(au.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(au.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(au.update_date, '%Y-%m-%d') = #{update_date}

order by au.id desc

mapper.insert:
insert into art_admin_user 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_admin_user where 1



	and ${key} is null


	and ${key} = #{value}
