mapper.sql:
select

from art_comment where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	c.id 
	, c.parent_id 
	, c.root_id 
	, c.article_id 
	, c.article_type 
	, c.content 
	, c.delete_status 
	, c.trace_code_i 
	, c.trace_code_u 
	, c.host_name 
	, c.create_user_id 
	, c.create_by 
	, c.create_date 
	, c.update_user_id 
	, c.update_by 
	, c.update_date 
from art_comment c 
where c.delete_status = 1

	and c.id = #{id}


	and c.parent_id = #{parent_id}


	and c.root_id = #{root_id}


	and c.article_id = #{article_id}


	and c.article_type = #{article_type}


	and c.content like concat('%', #{content}, '%')


	and c.trace_code_i like concat('%', #{trace_code_i}, '%')


	and c.trace_code_u like concat('%', #{trace_code_u}, '%')


	and c.host_name like concat('%', #{host_name}, '%')


	and c.create_user_id = #{create_user_id}


	and c.create_by like concat('%', #{create_by}, '%')


	and date_format(c.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(c.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(c.create_date, '%Y-%m-%d') = #{create_date}


	and c.update_user_id = #{update_user_id}


	and c.update_by like concat('%', #{update_by}, '%')


	and date_format(c.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(c.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(c.update_date, '%Y-%m-%d') = #{update_date}

order by c.id desc

mapper.insert:
insert into art_comment 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_comment where 1



	and ${key} is null


	and ${key} = #{value}
