mapper.sql:
select

from art_keyword where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	k.id 
	, k.work_id 
	, k.work_type 
	, k.keyword 
	, k.trace_code_i 
	, k.trace_code_u 
	, k.host_name 
	, k.create_user_id 
	, k.create_by 
	, k.create_date 
	, k.update_user_id 
	, k.update_by 
	, k.update_date 
from art_keyword k 
where 1 = 1

	and k.id = #{id}


	and k.work_id = #{work_id}


	and k.work_type = #{work_type}


	and k.keyword like concat('%', #{keyword}, '%')


	and k.trace_code_i like concat('%', #{trace_code_i}, '%')


	and k.trace_code_u like concat('%', #{trace_code_u}, '%')


	and k.host_name like concat('%', #{host_name}, '%')


	and k.create_user_id = #{create_user_id}


	and k.create_by like concat('%', #{create_by}, '%')


	and date_format(k.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(k.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(k.create_date, '%Y-%m-%d') = #{create_date}


	and k.update_user_id = #{update_user_id}


	and k.update_by like concat('%', #{update_by}, '%')


	and date_format(k.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(k.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(k.update_date, '%Y-%m-%d') = #{update_date}

order by k.id desc

mapper.insert:
insert into art_keyword 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_keyword where 1



	and ${key} is null


	and ${key} = #{value}
