mapper.sql:
select

from art_link where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select l.id , l.email , l.type , l.token , l.url , l.status , l.trace_code_i , l.trace_code_u , l.host_name , l.create_user_id , l.create_by , l.create_date , l.update_user_id , l.update_by , l.update_date from art_link l where 1 = 1
and l.id = #{id}
and l.email like concat('%', #{email}, '%')
and l.type = #{type}
and l.token like concat('%', #{token}, '%')
and l.url like concat('%', #{url}, '%')
and l.status = #{status}
and l.trace_code_i like concat('%', #{trace_code_i}, '%')
and l.trace_code_u like concat('%', #{trace_code_u}, '%')
and l.host_name like concat('%', #{host_name}, '%')
and l.create_user_id = #{create_user_id}
and l.create_by like concat('%', #{create_by}, '%')
and date_format(l.create_date, '%Y-%m-%d') >= #{create_date_from}
and date_format(l.create_date, '%Y-%m-%d') <= #{create_date_to}
and date_format(l.create_date, '%Y-%m-%d') = #{create_date}
and l.update_user_id = #{update_user_id}
and l.update_by like concat('%', #{update_by}, '%')
and date_format(l.update_date, '%Y-%m-%d') >= #{update_date_from}
and date_format(l.update_date, '%Y-%m-%d') <= #{update_date_to}
and date_format(l.update_date, '%Y-%m-%d') = #{update_date}
order by l.id desc

mapper.insert:
insert into art_link 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_link where 1



	and ${key} is null


	and ${key} = #{value}
