mapper.sql:
select

from art_region where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select r.id , r.city_id , r.root_id , r.parent_id , r.city_path , r.rank , r.city , r.city_en , r.city_type , r.gov_type , r.post , r.area_code , r.shorten , r.keyword , r.seq , r.delete_status , r.create_user_id , r.create_by , r.create_date , r.update_user_id , r.update_by , r.update_date from art_region r where r.delete_status = 1
and r.id like concat('%', #{id}, '%')
and r.city_id like concat('%', #{city_id}, '%')
and r.root_id like concat('%', #{root_id}, '%')
and r.parent_id like concat('%', #{parent_id}, '%')
and r.city_path like concat('%', #{city_path}, '%')
and r.rank = #{rank}
and r.city like concat('%', #{city}, '%')
and r.city_en like concat('%', #{city_en}, '%')
and r.city_type like concat('%', #{city_type}, '%')
and r.gov_type = #{gov_type}
and r.post like concat('%', #{post}, '%')
and r.area_code like concat('%', #{area_code}, '%')
and r.shorten like concat('%', #{shorten}, '%')
and r.keyword like concat('%', #{keyword}, '%')
and r.seq = #{seq}
and r.create_user_id = #{create_user_id}
and r.create_by like concat('%', #{create_by}, '%')
and date_format(r.create_date, '%Y-%m-%d') >= #{create_date_from}
and date_format(r.create_date, '%Y-%m-%d') <= #{create_date_to}
and date_format(r.create_date, '%Y-%m-%d') = #{create_date}
and r.update_user_id = #{update_user_id}
and r.update_by like concat('%', #{update_by}, '%')
and date_format(r.update_date, '%Y-%m-%d') >= #{update_date_from}
and date_format(r.update_date, '%Y-%m-%d') <= #{update_date_to}
and date_format(r.update_date, '%Y-%m-%d') = #{update_date}
order by r.id desc

mapper.insert:
insert into art_region 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_region where 1



	and ${key} is null


	and ${key} = #{value}
