mapper.sql:
select

from art_role_menu where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	rm.id 
	, rm.menu_id 
	, rm.role_id 
	, rm.trace_code_i 
	, rm.trace_code_u 
	, rm.host_name 
	, rm.create_user_id 
	, rm.create_by 
	, rm.create_date 
	, rm.update_user_id 
	, rm.update_by 
	, rm.update_date 
from art_role_menu rm 
where 1 = 1

	and rm.id = #{id}


	and rm.menu_id = #{menu_id}


	and rm.role_id = #{role_id}


	and rm.trace_code_i like concat('%', #{trace_code_i}, '%')


	and rm.trace_code_u like concat('%', #{trace_code_u}, '%')


	and rm.host_name like concat('%', #{host_name}, '%')


	and rm.create_user_id = #{create_user_id}


	and rm.create_by like concat('%', #{create_by}, '%')


	and date_format(rm.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(rm.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(rm.create_date, '%Y-%m-%d') = #{create_date}


	and rm.update_user_id = #{update_user_id}


	and rm.update_by like concat('%', #{update_by}, '%')


	and date_format(rm.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(rm.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(rm.update_date, '%Y-%m-%d') = #{update_date}

order by rm.id desc

mapper.insert:
insert into art_role_menu 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_role_menu where 1



	and ${key} is null


	and ${key} = #{value}
