mapper.sql:
select

from art_role where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select r.id , r.name , r.description , r.delete_status , r.trace_code_i , r.trace_code_u , r.host_name , r.create_user_id , r.create_by , r.create_date , r.update_user_id , r.update_by , r.update_date from art_role r where r.delete_status = 1
and r.id = #{id}
and r.name like concat('%', #{name}, '%')
and r.description like concat('%', #{description}, '%')
and r.trace_code_i like concat('%', #{trace_code_i}, '%')
and r.trace_code_u like concat('%', #{trace_code_u}, '%')
and r.host_name like concat('%', #{host_name}, '%')
and r.create_user_id = #{create_user_id}
and r.create_by like concat('%', #{create_by}, '%')
and date_format(r.create_date, '%Y-%m-%d') >= #{create_date_from}
and date_format(r.create_date, '%Y-%m-%d') <= #{create_date_to}
and date_format(r.create_date, '%Y-%m-%d') = #{create_date}
and r.update_user_id = #{update_user_id}
and r.update_by like concat('%', #{update_by}, '%')
and date_format(r.update_date, '%Y-%m-%d') >= #{update_date_from}
and date_format(r.update_date, '%Y-%m-%d') <= #{update_date_to}
and date_format(r.update_date, '%Y-%m-%d') = #{update_date}
order by r.id desc

mapper.insert:
insert into art_role 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_role where 1



	and ${key} is null


	and ${key} = #{value}
