mapper.sql:
select

from art_task where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	t.id 
	, t.name 
	, t.start_time 
	, t.finish_time 
	, t.storage_path 
	, t.file_url 
	, t.file_name 
	, t.percent 
	, t.progress_status 
	, t.exception 
	, t.trace_code_i 
	, t.trace_code_u 
	, t.host_name 
	, t.create_user_id 
	, t.create_by 
	, t.create_date 
	, t.update_user_id 
	, t.update_by 
	, t.update_date 
from art_task t 
where 1 = 1

	and t.id = #{id}


	and t.name like concat('%', #{name}, '%')


	and date_format(t.start_time, '%Y-%m-%d') >= #{start_time_from}


	and date_format(t.start_time, '%Y-%m-%d') <= #{start_time_to}


	and date_format(t.start_time, '%Y-%m-%d') = #{start_time}


	and date_format(t.finish_time, '%Y-%m-%d') >= #{finish_time_from}


	and date_format(t.finish_time, '%Y-%m-%d') <= #{finish_time_to}


	and date_format(t.finish_time, '%Y-%m-%d') = #{finish_time}


	and t.storage_path like concat('%', #{storage_path}, '%')


	and t.file_url like concat('%', #{file_url}, '%')


	and t.file_name like concat('%', #{file_name}, '%')


	and t.percent = #{percent}


	and t.progress_status = #{progress_status}


	and t.exception like concat('%', #{exception}, '%')


	and t.trace_code_i like concat('%', #{trace_code_i}, '%')


	and t.trace_code_u like concat('%', #{trace_code_u}, '%')


	and t.host_name like concat('%', #{host_name}, '%')


	and t.create_user_id = #{create_user_id}


	and t.create_by like concat('%', #{create_by}, '%')


	and date_format(t.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(t.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(t.create_date, '%Y-%m-%d') = #{create_date}


	and t.update_user_id = #{update_user_id}


	and t.update_by like concat('%', #{update_by}, '%')


	and date_format(t.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(t.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(t.update_date, '%Y-%m-%d') = #{update_date}

order by t.id desc

mapper.insert:
insert into art_task 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from art_task where 1



	and ${key} is null


	and ${key} = #{value}
